package com.mcfadyen.service;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import com.mcfadyen.domain.Product;
import com.mcfadyen.repository.ProductRepository;

/**
 * 
 * ProductService
 *
 * @since 30 de ago de 2016
 * @author Guilherme de Araujo Carvalho <guilhermecarvalho@finchsolucoes.com.br>
 *
 */
@Service
public class ProductService{

    @Autowired
    private ProductRepository productRepository;

    private static List<Product> products = new ArrayList<>();

    static {
        products.add(new Product("Maçã","data:image/jpeg;base64,", new BigDecimal(5)));
        products.add(new Product("Pera","data:image/jpeg;base64,", new BigDecimal(4)));
    }

    public void saveInitialBatch(){
        productRepository.save(products);
    }

    public List<Product> findAll(){
        return productRepository.findAll();
    }

    public Product findOne(String id){
        return productRepository.findOne(id);
    }
}
